svg.script:
//
window.addEventListener('load', init, false);
window.addEventListener('resize', applyResponsiveLayout, false);

function init () {
   console.log("init();");
   applyResponsiveLayout();
}

function applyResponsiveLayout() {
   console.log("applyResponsiveLayout();");
   var svgroot = document.querySelector("#svg_blomst_innmat_responsiv");
   var svg_width = svgroot.clientWidth;
   var svg_height = svgroot.clientHeight;
   var aspect = svg_width / svg_height;

   var data_transform_src = null;

   if( aspect > (4/3) ) {
      data_transform_src = "data-transform-wide";
   } else if (  aspect < (3/4)  ) {
      data_transform_src = "data-transform-tall";
   } else {
      data_transform_src = "data-transform-default";		
   }

   var els = document.querySelectorAll(".responsive");

   for (var i = 0; i < els.length; i++) {
      var el = els[i];
      el.setAttribute("transform", el.getAttribute(data_transform_src));
   }
}
//
